
public class Left_Shift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int x = 5;
        System.out.println("left shift 1 of x  is    :  "+(x<<1));
        System.out.println("left shift 2 of x  is    :  "+(x<<2));
        System.out.println("left shift 3 of x  is    :  "+(x<<3));
        System.out.println("left shift 4 of x  is    :  "+(x<<4));
        System.out.println("left shift 5 of x  is    :  "+(x<<5));
        System.out.println("left shift 6 of x  is    :  "+(x<<6));
        System.out.println("left shift 7 of x  is    :  "+(x<<7));
        System.out.println("left shift -1 of x  is   :  "+(-1<<1));
	}

}
