
public class Right_Shift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int x=3;
         System.out.println("Right shift 1 of x is     :  "+(x>>1));
         System.out.println("Right shift 2 of x is     :  "+(x>>2));
         System.out.println("Right shift 3 of x is     :  "+(x>>3));
         
         // for negative number
         int y  = -2;
         System.out.println("Right shift 1 of y is     :  "+(x>>1));
	}

}
